const TodoItem = () => {
    const dispatch = useDispatch();
    const todos = useSelector(state => state.todos);

    if(!todos || !todos.length){
        return <p>NO TODOS</p>
    }
    const handleClick = (id) => dispatch({
        type: 'REMOVE_TODO',
        payload: id
    })
    return (
        <div>
            {todos.map(todo => <TodoList handleClick={handleClick(todo.id)} label={todo.label}/>)}
        </div>
    )
}

function App(props) {
    return (
        <div className="App">
                <Header/>
                <AddTodo/>
                <TodoItem/>
        </div>
    );
}

export default App;
