import {connect} from "react-redux";
import './App.css'
import Header from "./components/Header/Header";
import AddTodo from './components/AddTodo/AddTodo'
import TodoList from "./components/TodoList/TodoList";

const ExtraComp = () => {
    return (
        <p style={{color: '#fff', marginTop: '150px', fontFamily: 'monospace'}}>Add Todo</p>
    )
}


function App(props) {

    const state = props.store.getState()
    const store = props.store

    const addTodo = (todoName) => {
        store.dispatch({type: 'ADD_TODO', text: todoName})
    }
    const TaskItem = () => {
        console.log(state)
        console.log(store)
        return (

            state.length !== 0 ?
                state.map((el, key) => {
                    return (
                        <TodoList
                            todo={el}
                            key={key}
                            // removeTodo={removeTodo}
                            // solveTodo={solveTodo}
                            // handleToggle={handleToggle}
                        />
                    )
                }) : <ExtraComp/>)
    }


    return (
        <div className="App">
            <Header/>
            <AddTodo onAddTodo={addTodo}/>
            <div className="forTaskItem">
                <TaskItem/>
            </div>
        </div>
    );
}

export default connect(
    state => ({
        textStore: state,
    }),
    dispatch => ({
        onAddTodo: (trackName) => {
            dispatch({type: 'ADD_TODO', text: trackName})
        },
    })
)(App);
