import plus from "../../add-removebg-preview.png";
import './AddTodo.css'

const AddTodo = (props) => {
    const handleSubmit = (e) => {
        e.preventDefault()
    }
    let trackInput = ""
    const addTrack = () => {
        console.log("Track was added:", trackInput.value)
        props.onAddTodo(trackInput.value)
        trackInput.value = ''
    }


    return (
        <form onSubmit={handleSubmit} id="float-label">
            <input type="text" ref={(input) => {trackInput = input}}/>
            <button className="addTask__btn" onClick={addTrack}>
                <img src={plus} width="40px" alt="add"/>
            </button>
        </form>
    )
}

export default AddTodo
