// import {Component} from "react";
// import plus from './add-removebg-preview.png'
import {connect} from "react-redux";
import './App.css'
import Header from "./components/Header/Header";
import AddTodo from './components/AddTodo/AddTodo'
import TodoList from "./components/TodoList/TodoList";

const ExtraComp = () => {

    return(
        <p style={{color: '#fff', marginTop: '150px', fontFamily: 'monospace'}}>Add Todo</p>
    )
}


function App(props) {
    console.log(props.textStore)

    const TaskItem = () => {
        return (
            props.textStore.length !== 0 ?
                props.textStore.map((el) => {
                    return (
                        <TodoList
                            todo={el}
                            key={el.id}
                            // removeTodo={removeTodo}
                            // solveTodo={solveTodo}
                            // handleToggle={handleToggle}
                        />
                    )
                }) : <ExtraComp/>)
    }



    return (
        <div className="App">
            <Header/>
            <AddTodo onAddTodo={props.onAddTodo}/>

            <TaskItem/>
        </div>
    );
}

export default connect(
    state => ({
        textStore: state,
    }),
    dispatch => ({
        onAddTodo: (trackName) => {
            dispatch({type: 'ADD_TODO', text: trackName, completed: false})
        }
    })
)(App);
