import {connect} from "react-redux";
import './App.css'
import Header from "./components/Header/Header";
import AddTodo from './components/AddTodo/AddTodo'
import TodoList from "./components/TodoList/TodoList";

const ExtraComp = () => {
    return (
        <p style={{color: '#fff', marginTop: '150px', fontFamily: 'monospace'}}>Add Todo</p>
    )
}


function App(props) {

    const TaskItem = () => {
        const state = props.store.getState()

        const onAddTodo = (todoName) => {
            state.dispatch({type: 'ADD_TODO', text: trackName})
        }

        return (
            state.length !== 0 ?
               state.map((el, key) => {
                    return (
                        <TodoList
                            todo={el}
                            key={key}
                            // removeTodo={removeTodo}
                            // solveTodo={solveTodo}
                            // handleToggle={handleToggle}
                        />
                    )
                }) : <ExtraComp/>)
    }


    return (
        <div className="App">
            <Header/>
            <AddTodo onAddTodo={props.onAddTodo}/>
            <div className="forTaskItem">
                <TaskItem/>
            </div>
        </div>
    );
}

export default App

