import tick from '../../img/green-tick.png'
import close from '../../img/close.svg'
import './TodoList.css'
import {useSelector} from "react-redux";

const TodoList = (props) => {
    const todos = useSelector(state => state.todos);

    if(!todos || !todos.length){
        return <p>ADD Todo</p>
    }

    return (
        <div  className="task-list__block">
            <div className={props.complete ? 'completed' : 'uncompleted'}
                 style={{
                     wordBreak: 'break-word',
                     width: '280px',
                     textAlign: 'start',
                     fontFamily: 'Courier, serif',

                 }}>
                <span onClick={props.handleTodo}  style={{cursor: "pointer"}}>{ props.label}</span>
            </div>

            <div className="task-list__todoBlock">

                <div onClick={() => props.handleSolve(props.id, props.label)}>
                    <button className="todoBlock__solve">
                        <img src={tick} width={"30px"} alt=""/>
                    </button>
                </div>

                <div onClick={() => props.handleClose(props.id)}>
                    <button className="todoBlock__close">
                        <img src={close} alt=""/>
                    </button>
                </div>

            </div>
        </div>
    )
}


