import {useDispatch, useSelector} from "react-redux";

const Counter = () => {
    const dispatch = useDispatch();
    const count = useSelector(state => state.count);

    const increment = () => {
        dispatch({
            type: 'INCREMENT'
        })
    }
    const decrement = () => {
        dispatch({
            type: 'DECREMENT'
        })
    }
    return (
        <div>
            <p>{count}</p>
            <button onClick={increment}>
                +
            </button>

            <button onClick={decrement}>
                -
            </button>


        </div>
    )
}

export default Counter
