// import {Component} from "react";
import {connect} from "react-redux";
import './App.css'
function App(props) {
    console.log(props.textStore)

    const addTrack = () => {
        console.log("Track was added:", trackInput.value)
        props.onAddTrack(trackInput.value)
        trackInput.value = ''
    }
    let trackInput = ""


    return (
        <div className="App">
            <div className="header__block">
                <h1>Todo App</h1>
            </div>
            <form id="float-label">
                <input type="text" ref={(input) => {trackInput = input}}/>
                <button className="addTask__btn" onClick={addTrack}>
                    <img src={plus} width="40px" alt="add"/>
                </button>
            </form>

            <ul>
                {
                    props.textStore.map((track, index) =>
                        <li key={index}> {track} </li>
                    )
                }
            </ul>
        </div>
    );
}

export default connect(
    state => ({
        textStore: state,
    }),
    dispatch => ({
        onAddTrack: (trackName) => {
            dispatch({type: 'ADD_TRACK', text: trackName})
        }
    })
)(App);
