// import {Component} from "react";
import plus from './add-removebg-preview.png'
import {connect} from "react-redux";
import './App.css'
import Header from "./components/Header/Header";
function App(props) {
    console.log(props.textStore)

    const addTrack = () => {
        console.log("Track was added:", trackInput.value)
        props.onAddTrack(trackInput.value)
        trackInput.value = ''
    }
    let trackInput = ""

    const handleSubmit = (e) => {
        e.preventDefault()
    }
    return (
        <div className="App">
            <Header/>
            <form onSubmit={handleSubmit} id="float-label">
                <input type="text" ref={(input) => {trackInput = input}}/>
                <button className="addTask__btn" onClick={addTrack}>
                    <img src={plus} width="40px" alt="add"/>
                </button>
            </form>

            <ul>
                {
                    props.textStore.map((track, index) =>
                        <div className="task-list__block" key={index}> {track} </div>
                    )
                }
            </ul>
        </div>
    );
}

export default connect(
    state => ({
        textStore: state,
    }),
    dispatch => ({
        onAddTrack: (trackName) => {
            dispatch({type: 'ADD_TRACK', text: trackName})
        }
    })
)(App);
