import './TodoList.css'
import {useDispatch, useSelector} from "react-redux";
import TodoList from "./TodoList";

const TodoItem = () => {
    const dispatch = useDispatch();
    const todos = useSelector(state => state.todos);

    if (!todos || !todos.length) {
        return <p>Add Todo</p>
    }
    const handleClose = (id) => dispatch({
        type: 'REMOVE_TODO',
        payload: id
    })

    const handleTodo = (id) => dispatch({
        type: 'SWITCH_TODO',
        payload: id
    })
    const handleSolve = (id, label) => {
        dispatch({
            type: 'SOLVE_TODO',
            payload: id
        })
        alert("Congratulations!!!\nYou have completed todo: " + label)
    }

return (
    <div>
        {todos.map(todo => <TodoList handleTodo={handleTodo} handleSolve={handleSolve} handleClose={handleClose} label={todo.label} id={todo.id}/>)}
    </div>
)
}
export default TodoItem
